import React from "react";
import { Box, Typography } from "@mui/material";

const LandingPage: React.FC = () => {
    return (
        <Box
            sx={{
                height: "100vh",
                width: "100%",
                backgroundColor: "#FFEEEE", // Light beige background
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                padding: "2rem",
                gap: "5rem", // Space between sections
            }}
        >
            {/* IVA and PARK Section */}
            <Box
                sx={{
                    display: "grid",
                    gridTemplateColumns: "1fr 1fr", // Two columns for side-by-side alignment
                    gap: "1rem", // Space between rows and columns
                    justifyItems: "center",
                }}
            >
                {/* Top Row */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem", // Adjust font size
                        gridColumn: "1 / 3", // Span both columns
                        textAlign: "center",
                    }}
                >
                    I
                </Typography>

                {/* Second Row */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "center",
                    }}
                >
                    V
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "center",
                    }}
                >
                    P
                </Typography>

                {/* Third Row */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "center",
                    }}
                >
                    A
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "center",
                    }}
                >
                    A
                </Typography>

                {/* Fourth Row */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "center",
                    }}
                >
                    R
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "center",
                    }}
                >
                    K
                </Typography>
            </Box>

            {/* Introduction Section */}
            <Box sx={{ maxWidth: "40%", textAlign: "left" }}>
                <Typography
                    variant="h5"
                    sx={{
                        color: "#000", // Black color
                        fontWeight: "bold",
                        marginBottom: "1rem",
                    }}
                >
                    Hello there,
                </Typography>
                <Typography
                    sx={{
                        color: "#000",
                        fontSize: "1.2rem",
                        lineHeight: 1.6,
                    }}
                >
                    I am a UI/UX designer passionate about turning complex ideas
                    into intuitive, engaging experiences. I combine design and
                    technology to create solutions that meaningfully connect
                    with users.
                </Typography>
            </Box>
        </Box>
    );
};

export default LandingPage;
