import React from "react";
import { Box, Typography } from "@mui/material";

const LandingPage: React.FC = () => {
    return (
        <Box
            sx={{
                height: "100vh",
                width: "100%",
                backgroundColor: "#FFEEEE", // Light pink background
                display: "flex",
                alignItems: "center",
                justifyContent: "space-around", // Adjust space between sections
                padding: "0 4rem",
            }}
        >
            {/* IVA and PARK Section */}
            <Box
                sx={{
                    display: "grid",
                    gridTemplateColumns: "1fr 1fr", // Two columns
                    gridTemplateRows: "repeat(4, auto)", // Four rows
                    gap: "0.5rem", // Space between rows
                }}
            >
                {/* Left Column: I, V, A */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "right", // Align text to the right
                    }}
                >
                    I
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "left", // Align text to the right
                    }}
                >
                    V
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "left", // Align text to the right
                    }}
                >
                    A
                </Typography>
                <Box></Box> {/* Empty space */}

                {/* Right Column: P, A, R, K */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "right", // Align text to the left
                    }}
                >
                    P
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "right", // Align text to the left
                    }}
                >
                    A
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "right", // Align text to the left
                    }}
                >
                    R
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "right", // Align text to the left
                    }}
                >
                    K
                </Typography>
            </Box>

            {/* Introduction Section */}
            <Box
                sx={{
                    maxWidth: "40%",
                    textAlign: "left",
                    marginLeft: "-3rem", // Move the text closer to IVA PARK
                }}
            >
                <Typography
                    variant="h5"
                    sx={{
                        color: "#000", // Black color
                        fontWeight: "bold",
                        marginBottom: "1rem",
                    }}
                >
                    Hello there,
                </Typography>
                <Typography
                    sx={{
                        color: "#000",
                        fontSize: "1.2rem",
                        lineHeight: 1.6,
                    }}
                >
                    I am a UI/UX designer passionate about turning complex ideas
                    into intuitive, engaging experiences. I combine design and
                    technology to create solutions that meaningfully connect
                    with users.
                </Typography>
            </Box>
        </Box>
    );
};

export default LandingPage;
