import React from "react";
import { Box, Typography } from "@mui/material";

const LandingPage: React.FC = () => {
    return (
        <Box
            sx={{
                height: "100vh",
                width: "100%",
                backgroundColor: "#FFEEEE", // Light beige background
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between", // Space between IVA PARK and the introduction section
                padding: "0 5rem", // Adjust left and right padding
            }}
        >
            {/* IVA and PARK Section */}
            <Box
                sx={{
                    display: "flex",
                    flexDirection: "row", // Row layout for IVA and PARK
                    alignItems: "center",
                    gap: "2rem", // Space between IVA and PARK
                }}
            >
                {/* IVA Column */}
                <Box
                    sx={{
                        display: "flex",
                        flexDirection: "column",
                        alignItems: "flex-end", // Align IVA to the right of its column
                        gap: "0.5rem", // Space between letters
                    }}
                >
                    <Typography
                        variant="h1"
                        sx={{
                            color: "#4A2040", // Dark purple
                            fontWeight: "bold",
                            fontSize: "8rem", // Adjust font size
                        }}
                    >
                        I
                    </Typography>
                    <Typography
                        variant="h1"
                        sx={{
                            color: "#4A2040",
                            fontWeight: "bold",
                            fontSize: "8rem",
                        }}
                    >
                        V
                    </Typography>
                    <Typography
                        variant="h1"
                        sx={{
                            color: "#4A2040",
                            fontWeight: "bold",
                            fontSize: "8rem",
                        }}
                    >
                        A
                    </Typography>
                </Box>

                {/* PARK Column */}
                <Box
                    sx={{
                        display: "flex",
                        flexDirection: "column",
                        alignItems: "flex-start", // Align PARK to the left of its column
                        gap: "0.5rem", // Space between letters
                    }}
                >
                    <Typography
                        variant="h1"
                        sx={{
                            color: "#4A2040", // Dark purple
                            fontWeight: "bold",
                            fontSize: "8rem",
                        }}
                    >
                        P
                    </Typography>
                    <Typography
                        variant="h1"
                        sx={{
                            color: "#4A2040",
                            fontWeight: "bold",
                            fontSize: "8rem",
                        }}
                    >
                        A
                    </Typography>
                    <Typography
                        variant="h1"
                        sx={{
                            color: "#4A2040",
                            fontWeight: "bold",
                            fontSize: "8rem",
                        }}
                    >
                        R
                    </Typography>
                    <Typography
                        variant="h1"
                        sx={{
                            color: "#4A2040",
                            fontWeight: "bold",
                            fontSize: "8rem",
                        }}
                    >
                        K
                    </Typography>
                </Box>
            </Box>

            {/* Introduction Section */}
            <Box
                sx={{
                    maxWidth: "40%",
                    textAlign: "left",
                    marginLeft: "-5rem", // Move the text to the left
                }}
            >
                <Typography
                    variant="h5"
                    sx={{
                        color: "#000", // Black color
                        fontWeight: "bold",
                        marginBottom: "1rem",
                    }}
                >
                    Hello there,
                </Typography>
                <Typography
                    sx={{
                        color: "#000",
                        fontSize: "1.2rem",
                        lineHeight: 1.6,
                    }}
                >
                    I am a UI/UX designer passionate about turning complex ideas
                    into intuitive, engaging experiences. I combine design and
                    technology to create solutions that meaningfully connect
                    with users.
                </Typography>
            </Box>
        </Box>
    );
};

export default LandingPage;
