import { createTheme } from "@mui/material/styles";

const theme = createTheme({
    palette: {
        primary: {
            main: "#4A2040", // Dark purple
        },
        text: {
            primary: "#000000", // Black
        },
    },
    typography: {
      fontFamily: "'Roboto', sans-serif, 'Playfair Display', serif", // Combine both fonts
    },

});

export default theme;
