import React from "react";
import { Box, Typography } from "@mui/material";

const LandingPage: React.FC = () => {
    return (
        <Box
            sx={{
                height: "100vh",
                width: "100%",
                backgroundColor: "#FFEEEE", // Light pink background
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between", // Space between IVA PARK and text
                padding: "0 4rem", // Padding on the sides
            }}
        >
            {/* IVA and PARK Section */}
            <Box
                sx={{
                    display: "grid",
                    gridTemplateColumns: "1fr 1fr", // Two columns for alignment
                    gridTemplateRows: "repeat(5, auto)", // 5 rows
                    gap: "0.5rem", // Space between rows and columns
                    alignItems: "center",
                }}
            >
                {/* First Row */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        gridColumn: "1 / span 2", // Span across both columns
                        textAlign: "center",
                    }}
                >
                    I
                </Typography>

                {/* Second Row */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040", // Dark purple
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "right",
                    }}
                >
                    V
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "left",
                    }}
                >
                    P
                </Typography>

                {/* Third Row */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "right",
                    }}
                >
                    A
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "left",
                    }}
                >
                    A
                </Typography>

                {/* Fourth Row */}
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "right",
                    }}
                >
                    R
                </Typography>
                <Typography
                    variant="h1"
                    sx={{
                        color: "#4A2040",
                        fontWeight: "bold",
                        fontSize: "8rem",
                        textAlign: "left",
                    }}
                >
                    K
                </Typography>
            </Box>

            {/* Introduction Section */}
            <Box
                sx={{
                    maxWidth: "40%",
                    textAlign: "left",
                    marginLeft: "-2rem", // Move the text closer to IVA PARK
                }}
            >
                <Typography
                    variant="h5"
                    sx={{
                        color: "#000", // Black color
                        fontWeight: "bold",
                        marginBottom: "1rem",
                    }}
                >
                    Hello there,
                </Typography>
                <Typography
                    sx={{
                        color: "#000",
                        fontSize: "1.2rem",
                        lineHeight: 1.6,
                    }}
                >
                    I am a UI/UX designer passionate about turning complex ideas
                    into intuitive, engaging experiences. I combine design and
                    technology to create solutions that meaningfully connect
                    with users.
                </Typography>
            </Box>
        </Box>
    );
};

export default LandingPage;
